import React, { Component } from "react";
import { WriterWrapper, WriterItem } from "../style";
import { connect } from "react-redux";
import { actionCreators } from "../store";

class Writer extends Component {
  componentDidMount() {
    this.props.getWriterList();
  }
  render() {
    const { wirterList, writerPage, writeTotal } = this.props;
    let currentList = [];
    const pageSize = 5;
    const jsWirterList = wirterList.toJS();
    console.log(jsWirterList);
    for (let i = (writerPage - 1) * pageSize; i < writerPage * pageSize; i++) {
      currentList.push(
        <WriterItem>
          <img src={jsWirterList[i].avatar_source} alt="" />
          <div className="info">
            <h5>{jsWirterList[i].nickname}</h5>
            <p>{jsWirterList[i].total_likes_count / 1000}喜欢</p>
          </div>
        </WriterItem>
      );
    }
    return (
      <WriterWrapper>
        <WriterItem>
          <img
            src="https://upload.jianshu.io/users/upload_avatars/2631077/dc99c361412c?imageMogr2/auto-orient/strip|imageView2/1/w/96/h/96/format/webp"
            alt=""
          />
          <div className="info">
            <h5>1</h5>
            <p>2</p>
          </div>
        </WriterItem>
      </WriterWrapper>
    );
  }
}

const mapState = state => ({
  wirterList: state.getIn(["home", "writerList"]),
  writerPage: state.getIn(["home", "writerPage"]),
  writeTotal: state.getIn(["home", "writeTotal"])
});

const mapDispatch = dispatch => ({
  getWriterList() {
    dispatch(actionCreators.getWriterList());
  }
});

export default connect(
  mapState,
  mapDispatch
)(Writer);
