import * as actionTypes from "./actionTypes";
import axios from "axios";
import {
  fromJS
} from "immutable";


export const getList = () => {
  return dispatch => {
    axios
      .get("/api/recommendUser.json")
      .then(res => {
        const data = res.data;
        dispatch(getWriterList(data.data));
      })
      .catch(err => {
        console.log(err, "error");
      });
  };
};

const getWriterList = data => ({
  type: actionTypes.CHANGE_LIST,
  data: fromJS(data),
  total: data.length,
});
