import React, { Component } from "react";
import { TopicWrapper, TopicItem } from "../style";
import { connect } from "react-redux";

class Topic extends Component {
  render() {
    return (
      <TopicWrapper>
        {this.props.list.map(item => {
          return (
            <TopicItem>
              <img
                className="topic-pic"
                src="https://upload.jianshu.io/users/upload_avatars/8972166/027bf05a-67fa-459e-b61c-c73ce267367c.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/96/h/96/format/webp"
              />
              社会热点
            </TopicItem>
          );
        })}
      </TopicWrapper>
    );
  }
}

const mapState = state => ({
  list: state.getIn(["home", "topicList"])
});
export default connect(
  mapState,
  null
)(Topic);
