import React, { Component } from "react";
import { WriterWrapper, WriterItem, WriterHeader } from "../style";
import { connect } from "react-redux";
import { actionCreators } from "../store";

class Writer extends Component {
  componentDidMount() {
    this.props.getWriterList();
  }
  render() {
    const { wirterList, writerPage, writeTotal, changeList } = this.props;
    let currentList = [];
    const pageSize = 5;
    const jsWirterList = wirterList.toJS();
    if (jsWirterList.length) {
      for (
        let i = (writerPage - 1) * pageSize;
        i < writerPage * pageSize;
        i++
      ) {
        currentList.push(
          <WriterItem key={jsWirterList[i].id}>
            <img src={jsWirterList[i].avatar_source} alt="" />
            <div className="info">
              <h5>{jsWirterList[i].nickname}</h5>
              <p>{jsWirterList[i].total_likes_count}喜欢</p>
            </div>
          </WriterItem>
        );
      }
    }
    return (
      <WriterWrapper>
        <WriterHeader>
          <span>推荐作者</span>
          <span
            className="btn"
            onClick={() => {
              changeList(writerPage, writeTotal);
            }}>
            换一批
          </span>
        </WriterHeader>
        {currentList}
      </WriterWrapper>
    );
  }
}

const mapState = state => ({
  wirterList: state.getIn(["home", "writerList"]),
  writerPage: state.getIn(["home", "writerPage"]),
  writeTotal: state.getIn(["home", "writeTotal"])
});

const mapDispatch = dispatch => ({
  getWriterList() {
    dispatch(actionCreators.getWriterList());
  },
  changeList() {}
});

export default connect(
  mapState,
  mapDispatch
)(Writer);
